SELECT * FROM customer;

--Q1. What is the total revenue generated by male vs.female customers?
SELECT gender, SUM(purchase_amount) as revenue FROM customer
GROUP BY gender;

--Q2. Which customers used a discount but still spent more than the average purchase amount?
SELECT customer_id, purchase_amount FROM customer
WHERE purchase_amount >= (SELECT AVG(purchase_amount) FROM customer) AND discount_applied = 'Yes';

--Q3. Which are the top 5 products with the highest average review rating?
SELECT item_purchased, ROUND(AVG(review_rating::numeric),2) AS average_review FROM customer
GROUP BY item_purchased 
ORDER BY AVG(review_rating) DESC
LIMIT 5;

--Q4. Compare the average Purchase Amount between Standard and Express Shipping 
SELECT ROUND(AVG(purchase_amount),2), shipping_type FROM customer
WHERE shipping_type IN('Standard', 'Express')
GROUP BY shipping_type;

--Q5. Do subscribed customers spend more? Compare average spend and total revenue between subscribiers and non-subscribers.
SELECT subscription_status, 
COUNT(customer_id) AS total_customers,
ROUND(AVG(purchase_amount),2) AS average_spend, 
ROUND(SUM(purchase_amount),2) AS revenue 
FROM customer
GROUP BY subscription_status 
ORDER BY revenue, average_spend DESC;

--Q6. Which 5 products have the highest percentage of purchases with discounts applied? 
-- This question is helping us which products are heavily rely on discount 
SELECT item_purchased, 
ROUND(100 * SUM(CASE WHEN discount_applied = 'Yes' THEN 1 ELSE 0 END)/COUNT(*),2) AS discount_rate
FROM customer 
GROUP BY item_purchased
ORDER BY discount_rate DESC
LIMIT 5;

--Q7. Segment cusgomers into New, Returning, and Loyal based on their total number of previews purchase, and show the count of each segment
WITH customer_type AS(
SELECT customer_id, previous_purchases,
CASE 
	WHEN previous_purchases = 1 THEN 'New'
	WHEN previous_purchases BETWEEN 2 AND 10 THEN 'Returning'
	ELSE 'Loyal'
	END AS customer_segment
FROM customer
)
SELECT customer_segment, COUNT(*) AS "Numer of Customers"
FROM customer_type
GROUP BY customer_segment;

--Q8. What are the top 3 most purchased products within each category?
WITH item_counts AS (
SELECT category, item_purchased,
COUNT(customer_id) AS total_orders,
ROW_NUMBER() OVER(PARTITION BY category ORDER BY COUNT(customer_id) DESC) AS item_rank
FROM customer
GROUP BY category, item_purchased
)
SELECT item_rank, category, item_purchased, total_orders
FROM item_counts
WHERE item_rank <= 3;

--Q9. Are customers who are repeat buyers(more than 5 previous purchases) also likely to subscribe?
SELECT subscription_status, COUNT(customer_id) FROM customer
WHERE previous_purchases > 5
GROUP BY subscription_status;

--Q10. What is the revenue contribution of each age group?
SELECT age_group, SUM(purchase_amount) as total_revenue FROM customer
GROUP BY age_group
ORDER BY total_revenue DESC;








